const checkChilledComplete = () => {
	return data.chilledStorageGrid.every(chilled => {
		let actionCheck = false

		if(chilled.action === 'corrective') {
			actionCheck = Object.keys(chilled.selectBoxes).some(key => chilled.selectBoxes[key])
		} else if(chilled.action === 'preemptive') {
			actionCheck = Object.keys(chilled.preEmptiveActions1).some(key => chilled.preEmptiveActions1[key])
		}

		return !!chilled.product && ((typeof chilled.unitTitle === 'number' && !chilled.action) || actionCheck)
	})
}

