	(data.settings.active1 ? checkFrozenComplete() : true) &&
	(data.settings.active2 ? checkHotHoldingComplete() : true) &&
	(data.settings.active3 ? checkSimpleComplete() : true) &&
	(data.settings.active4 ? checkDishwashersComplete() : true)
) {
	value = 'complete'
} else {
	value = 'in progress'
}

actions.schemaDefinition:
	schema = { disabled: !(data.status === 'complete') }

if(data.status === 'complete') {
	instance.element.querySelector('button').setAttribute('style', 'background: #41fc82 !important; border-color: #41fc82 !important; color: black !important; opacity: 1')
} else {
	instance.element.querySelector('button').setAttribute('style', 'background: #2f925e !important; border-color: #2f925e !important; color: white !important; opacity: .4')
}
