const scores = []

data.questionsGrid.forEach(questionGrid => {
  questionGrid.questionPanelGrid.forEach(questionPanel => {
    if(questionPanel.standard !== 'includeNA') {
     scores.push(questionPanel.score) 
    }
  })
})

let template = ''

if(scores.length === 0 || scores.every(score =>  ['', false, undefined].includes(score))) {
  template = `
	 <div style="display: flex; align-items: center; background: #353C70; color: white; border-radius: .25rem;;">
          <div style="background: #5046E6; border-radius: .25rem; color: white; padding: .625rem 1rem;">
              Score
          </div>
          <div style="padding: 0 1rem">
            0%
          </div>
      </div>
  `
} else {
  const filteredScores = scores.map(score => typeof score !== 'number' ? 0 : score)
  const average = filteredScores.length > 0 ? (filteredScores.reduce((a, b) => a + b) / filteredScores.length) : 0
  const rounded = average % 1 === 0 ? average : Number(average.toFixed(1))
  template = `
     <div style="display: flex; align-items: center; background: #353C70; color: white; border-radius: .25rem;;">
          <div style="background: #5046E6; border-radius: .25rem; color: white; padding: .625rem 1rem;">
              Score
          </div>
          <div style="padding: 0 1rem">
            ${rounded}%
          </div>
      </div>
`
}

schema = { content: template }

