let label = ''

const parentIndex = instance.parent.parent.parent._rowIndex;
const index = instance.rowIndex;

const contentGroup = data?.settings?.contentSettingsGrid ? data.settings.contentSettingsGrid[parentIndex] : {}
const questionsArray = !!contentGroup.questions ? contentGroup.questions.split('\n') : []

const labelRaw = questionsArray[index] ? questionsArray[index] : ''

const descriptionExp = new RegExp('\{.+\}')

const matched = labelRaw.match(descriptionExp)

if(matched) {
	const topLine = labelRaw.split(matched[0])[0]
	const bottomLine = matched[0].slice(1, matched[0].length - 1)

	label = `
		<div>${ topLine }</div>
		<div style="font-size: 12px">${ bottomLine }</div>
	`
} else {
	label = labelRaw
}

schema = { content: label }

