const replaceIds = () => {
    const labels = instance.element.querySelectorAll('.form-radio label')

		const rootIndex = instance.parent.parent.parent._rowIndex

		if(!instance.initialIds) {
			instance.initialIds = [...labels].map((label, labelIndex) => label.children[0].id)
		}

	    for (let i = 0; i < labels.length; i++) {
			const label = labels[i]

			label.children[0].id = !!instance.initialIds[i]? instance.initialIds[i]  + `-${rootIndex}` : `answer-option-${i}-${rootIndex}`
			label.htmlFor = !!instance.initialIds[i]? instance.initialIds[i] + `-${rootIndex}` : `answer-option-${i}-${rootIndex}`
	    }

}

setTimeout(() => {
	replaceIds()
}, 100)

