actions.schemaDefinition:
let template;
const status = row.status;

if (status === "Pass") {
  template = `
 			<span>
 				<span> 
 					Testing
 				</span>
 				<span style="margin-left: .25rem; display: inline-block; background: #41fc82; color: black; padding: .25rem .7rem; font-size: 12px; border-radius: 1rem">
 					Pass
 				</span>
 			</span>			
 		 `;
} else if (status === "Fail") {
  template = `
		<span>
			<span> 
				Testing
			</span>
			<span style="margin-left: .25rem; display: inline-block; background: #f44336; color: white; padding: .25rem .7rem; font-size: 12px; border-radius: 1rem">
				Fail
			</span>
		 </span>				
 	 	`;
} else {
  template = `
		<span>
			Testing
		</span>
 	`;
}

schema = { title: template };

