actions.schemaDefinition:
let values = data.settings.goodsCategoriesArray.map(item => {
  if(!!item) {
    return { label: item, value: item.split(' ').join() }
  } else {
    return false
  }
})

values = values.filter(value => value !== false)

schema = { data: { values } }

components.customConditional:
const getCorrective = () => {
  let limits = null

  if(data.temperatureType1 === 'chilled') {
    limits = data.settings.chilledLimits
  } else if(data.temperatureType1 === 'frozen') {
    limits = data.settings.frozenLimits
  } else if(data.temperatureType1 === 'hot') {
    limits = data.settings.hotLimits
  }

  if(limits === null) {
    return false
  } else if(data.frozenState === 'partiallyThawed' || data.frozenState === 'notFrozen') {
    return true 
  } else {
    return  limits.criticalLimits && typeof limits.upperLimit === 'number' && typeof data.goodsTemperature === 'number' && data.goodsTemperature >= limits.upperLimit 
  }  
} 

const getPreemptive = () => {
  let limits = null

  if(data.temperatureType1 === 'chilled') {
    limits = data.settings.chilledLimits
  } else if(data.temperatureType1 === 'frozen') {
    limits = data.settings.frozenLimits
  } else if(data.temperatureType1 === 'hot') {
    limits = data.settings.hotLimits
  }

  if(limits === null) {
    return false
  } else {
    return  limits.actionLimits && typeof limits.lowerLimit1 === 'number' && typeof limits.upperLimit1 === 'number' && typeof data.goodsTemperature === 'number' && (data.goodsTemperature > limits.lowerLimit1 && data.goodsTemperature < limits.upperLimit1) 
  }  
}

show = getPreemptive() || getCorrective()

