actions.schemaDefinition:
let option = []

if(data.temperatureType1 === 'chilled') {
  option = data.settings.chilledLimits.option2
} else if(data.temperatureType1 === 'frozen') {
  option = data.settings.frozenLimits.option2
} else if(data.temperatureType1 === 'hot') {
  option = data.settings.hotLimits.option2
}

let values = option.map(o => {
  if(!!o) {
    return {
      label: o,
      value: o
    }
  } else {
    return false
  }
})

values = values.filter(value => value !== false)
schema = { values }

